export type PostsType = {
    id: number
    message: string
    likesCount: number
}
export type DialogType = {
    id: number
    name: string
}
export type MessageType = {
    id: number
    message: string
}
export type ProfilePageType = {
    posts: Array<PostsType>
    newPostText: string
}
export type DialogsPageType = {
    dialogs: Array<DialogType>
    messages: Array<MessageType>
    newMessageBody:string
}
export type RootStateType = {
    profilePage: ProfilePageType
    dialogsPage: DialogsPageType
}
export type RootStoreType = {
    _state: RootStateType
    getState: () => RootStateType
    _calSubscriber: (state: RootStateType) => void
    subscriber: (observer: (state: RootStateType) => void) => void
    dispatch : (action: ActionsTypes) => void
}



export type ActionsTypes = ReturnType<typeof addPostActionCreator> | ReturnType<typeof UpdateNewPostTextActionCreator>

const ADD_POST = "ADD-POST";
const UPDATE_NEW_POST = "UPDATE-NEW-POST-TEXT";
const UPDATE_NEW_MESSAGE_BODY = "UPDATE-NEW-MESSAGE-BODY"
const  SEND_MESSAGE= "SEND-MESSAGE"

const store: RootStoreType = {
    _state: {
        profilePage: {
            posts: [
                {id: 1, message: "Hi how are you", likesCount: 11},
                {id: 2, message: "It's my first message", likesCount: 12},
            ],
            newPostText: ""
        },
        dialogsPage: {
            dialogs: [
                {id: 1, name: "Taras"},
                {id: 2, name: "Victor"},
                {id: 3, name: "Andrey"},
                {id: 4, name: "Petya"},
                {id: 5, name: "Vasya"},
                {id: 6, name: "Stepan"},
            ],
            messages: [
                {id: 1, message: 'Hi'},
                {id: 2, message: 'How is your it-kamasutra'},
                {id: 3, message: 'Yo'},
                {id: 4, message: 'Yo'},
                {id: 5, message: 'Yo'},
                {id: 6, message: 'Yo'},
                {id: 7, message: 'Yo'},
            ],
            newMessageBody: ''
        }
    },
    getState() {
        return this._state
    },
    _calSubscriber(state) {

    },
    subscriber(observer) {
        this._calSubscriber = observer
    },
    dispatch (action) {
        if (action.type === ADD_POST) {
            let newPost = {
                id: 5,
                message: this._state.profilePage.newPostText,
                likesCount: 0
            }
            this._state.profilePage.posts.push(newPost)
            this._state.profilePage.newPostText = ""
            this._calSubscriber(this._state)
        }
        else if (action.type === UPDATE_NEW_POST) {
            this._state.profilePage.newPostText = action.newText
            this._calSubscriber(this._state)
        } else if (action.type === UPDATE_NEW_MESSAGE_BODY) {
            this._state.dialogsPage.newMessageBody = action.body
            this._calSubscriber(this._state)
        }
    }
}

export let addPostActionCreator = ()=> {
    return {
        type: ADD_POST
    } as const
}
export const UpdateNewPostTextActionCreator = (text:string)=> {
    return {
        type : UPDATE_NEW_POST, newText : text
    } as const
}


export default store
