import React from 'react';
import s from './Dialogs.module.css'
import DialogItem from "./DialogItem/DialogItem";
import Message from './Message/Message';
import {DialogsPageType} from "../../redux/state";

type DialogsPropsType = {
    state: DialogsPageType
}

const Dialogs: React.FC<DialogsPropsType> = (props) => {

    let dialogsElement = props.state.dialogs.map(d => <DialogItem name = {d.name} id = {d.id}/>)
    let messagesElement = props.state.messages.map(m => <Message message= {m.message} id = {m.id}/>)
    let newMessageBody = props.state.newMessageBody;

    let onSendMessageClick = () => {

    }
    return (
        <div className = {s.dialogs}>
            <div className = {s.dialogsItems}>
                {dialogsElement}

            </div>
            <div className={s.messages}>
                <div>{messagesElement}</div>
                <div>
                    <div><textarea value = {newMessageBody} placeholder= "Enter your message"></textarea></div>
                    <div><button onClick={onSendMessageClick }></button></div>
                </div>
            </div>

        </div>
    );
};

export default Dialogs;
